import React, { useState, useEffect } from 'react';
import './Train.css';

const Train = () => {
  const [resource, setResource] = useState({
    cpu: 1,
    memory: 500,
    total_memory: 500,
    gpu: 50,
    epoch: 1
  });

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('/get_resource');
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        setResource(data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  return (
    <div className="container">
      <h1>Train 화면입니다</h1>
      <div className="resource-container">
        {/* 메모리 */}
        <div className="circular-progress">
          <svg width="200" height="200">
            <circle
              className="stroke"
              cx="100"
              cy="100"
              r="89"
              style={{
                strokeDasharray: `${2 * Math.PI * 89 * (resource.cpu / 100)} ${2 * Math.PI * 89}`
              }}
            />
          </svg>
          <div className="inner">
            <p>{resource.cpu}%</p>
          </div>
        </div>
        <div className="circular-progress">
          <svg width="200" height="200">
            <circle
              className="stroke"
              cx="100"
              cy="100"
              r="89"
              style={{
                strokeDasharray: `${2 * Math.PI * 89 * (resource.memory / resource.total_memory)} ${
                  2 * Math.PI * 89
                }`
              }}
            />
          </svg>
          <div className="inner">
            <p>{resource.memory} MB</p>
          </div>
        </div>
        <div className="circular-progress">
          <svg width="200" height="200">
            <circle
              className="stroke"
              cx="100"
              cy="100"
              r="89"
              style={{
                strokeDasharray: `${2 * Math.PI * 89 * (resource.gpu / 100)} ${2 * Math.PI * 89}`
              }}
            />
          </svg>
          <div className="inner">
            <p>{resource.gpu}%</p>
          </div>
        </div>
        <div className="circular-progress">
          <svg width="200" height="200">
            <circle
              className="stroke"
              cx="100"
              cy="100"
              r="89"
              style={{
                strokeDasharray: `${2 * Math.PI * 89 * (resource.epoch / 100)} ${2 * Math.PI * 89}`
              }}
            />
          </svg>
          <div className="inner">
            <p>{resource.epoch}</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Train;
