import React, { useState, useEffect } from 'react';
import './Train.css';

const Train = () => {
  const [resource, setResource] = useState({
    cpu: 1,
    memory: 500,
    total_memory: 500,
    gpu: 50,
    epoch: 1
  });
  const [inputCommand, setInputCommand] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('/get_resource');
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        setResource(data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();

    const intervalId = setInterval(fetchData, 1000);

    return () => clearInterval(intervalId);
  }, []);

  const handleInputChange = (e) => {
    setInputCommand(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('/train', {
        method: 'GET'
      });

      if (!response.ok) {
        throw new Error('Network response was not ok');
      }

      const data = await response.json();
      if (data.train) {
        console.log('Training started');
      }
    } catch (error) {
      console.error('Error starting training:', error);
    }
  };

  ////////// jsx
  return (
    <div className="container">
      <h1>Train 화면입니다</h1>
      <div className="resource-container">
        <div className="circular-progress">
          <div className="inner">
            <p>{resource.cpu}%</p>
          </div>
          <div className="outer" style={{ animationDuration: `${resource.cpu * 20}ms` }}></div>
        </div>
        <div className="circular-progress">
          <div className="inner">
            <p>{resource.memory} MB</p>
          </div>
          <div className="outer" style={{ animationDuration: `${resource.memory * 2}ms` }}></div>
        </div>
        <div className="circular-progress">
          <div className="inner">
            <p>{resource.gpu}%</p>
          </div>
          <div className="outer" style={{ animationDuration: `${resource.gpu * 20}ms` }}></div>
        </div>
        <div className="circular-progress">
          <div className="inner">
            <p>{resource.epoch}</p>
          </div>
          <div className="outer" style={{ animationDuration: `${resource.epoch * 100}ms` }}></div>
        </div>
      </div>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={inputCommand}
          onChange={handleInputChange}
          placeholder="명령어를 입력하세요 (예: python train.py)"
          className="command-input"
        />
        <button type="submit" className="submit-button">
          실행
        </button>
      </form>
    </div>
  );
};

export default Train;
