import React, { useState, useEffect } from 'react';
import { Doughnut } from 'react-chartjs-2';

const Train = () => {
  const [resource, setResource] = useState({
    cpu: 0,
    memory: 0,
    total_memory: 0,
    gpu: 0,
    epoch: 0
  });

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('/get_resource');
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        setResource(data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();

    const intervalId = setInterval(fetchData, 1000);

    return () => clearInterval(intervalId);
  }, []);

  const cpuData = {
    labels: ['Used', 'Unused'],
    datasets: [
      {
        data: [resource.cpu, 100 - resource.cpu],
        backgroundColor: ['#FF6384', '#36A2EB'],
        hoverBackgroundColor: ['#FF6384', '#36A2EB']
      }
    ]
  };

  return (
    <div>
      <h1>Main 화면입니다</h1>
      <div>
        <Doughnut data={cpuData} />
        <p>메모리 사용량: {resource.memory} MB</p>
        <p>전체 메모리: {resource.total_memory} MB</p>
        <p>GPU 사용량: {resource.gpu}</p>
        <p>Epoch: {resource.epoch}</p>
      </div>
    </div>
  );
};

export default Train;
