import React, { useState } from 'react';

const Train = () => {
  const [cpu, setCpu] = useState(0);
  const [memory, setMemory] = useState(0);
  const [totalMemory, setTotalMemory] = useState(0);
  const [gpu, setGpu] = useState(0);
  const [epoch, setEpoch] = useState(0);
  const [command, setCommand] = useState('');

  const fetchResource = async () => {
    try {
      const response = await fetch('http://localhost:5000/get_resource');
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }

      const data = await response.json();
      setCpu(data.cpu);
      setMemory(data.memory);
      setTotalMemory(data.total_memory);
      setGpu(data.gpu);
      setEpoch(data.epoch);
    } catch (error) {
      console.error('Error fetching resource:', error);
    }
  };

  const handleSubmit = async () => {
    try {
      const response = await fetch('http://localhost:5000/train', {
        method: 'GET'
      });

      if (!response.ok) {
        throw new Error('Network response was not ok');
      }

      const data = await response.json();
      console.log(data);

      // 측정값 업데이트
      fetchResource();
    } catch (error) {
      console.error('Error starting training:', error);
    }
  };

  const handleInputChange = (e) => {
    setCommand(e.target.value);
  };

  return (
    <div>
      <input type="text" value={command} onChange={handleInputChange} placeholder="Enter command" />
      <button onClick={handleSubmit}>Execute Command</button>
      <div>
        <h2>Resource Usage</h2>
        <p>CPU: {cpu}%</p>
        <p>Memory: {memory} MB</p>
        <p>Total Memory: {totalMemory} MB</p>
        <p>GPU: {gpu}%</p>
        <p>Epoch: {epoch}</p>
      </div>
    </div>
  );
};

export default Train;
